var sqlite3 = require('sqlite3').verbose();
var userDB = new sqlite3.Database('auth.sqlite3');

// Create the user table (if it doesn't yet exist)
// The hashes generated by our BCrypt algorithm are 
// only 60 characters in length, so we can limit 
// our storage to that amount.
userDB.run(`CREATE TABLE IF NOT EXISTS users (
          id INTEGER PRIMARY KEY,
          username VARCHAR(75),
          cryptedPassword VARCHAR(60),
          role VARCHAR(10)
        )`, (err) => {console.error(err);}
);

module.exports = userDB;